contracts/DAOAirdrop.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity =0.8.9;

import './interfaces/IDAOFactory.sol';
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/MerkleProofUpgradeable.sol";

contract DAOAirdrop {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    address public immutable factoryAddress;
    mapping(uint256 => Airdrop) public airdrops;
    mapping(uint256 => mapping(address => bool)) private claimedMap;

    constructor(address factoryAddress_) {
        factoryAddress = factoryAddress_;
    }

    struct Airdrop {
        address token;
        uint256 tokenStaked;
        uint256 tokenClaimed;
        bytes32 merkleRoot;
        uint256 startTime;
        uint256 endTime;

        address creator;
    }

    event CreateAirdrop(
        address indexed creator,
        uint256 indexed airdropId,
        address token,
        uint256 amount,
        uint256 startTime,
        uint256 endTime
    );
    event SettleAirdrop(
        uint256 indexed airdropId,
        uint256 amount,
        bytes32 merkleTreeRoot
    );
    event Claimed(
        uint256 indexed airdropId,
        uint256 index,
        address account,
        uint256 amount
    );

    function createAirdrop(
        uint256 airdropId_,
        address token_,
        uint256 amount_,
        uint256 startTime_,
        uint256 endTime_,
        bytes calldata signature_
    ) external payable {
        require(airdrops[airdropId_].creator == address(0), 'DAOAirdrop: duplicate airdrop id.');
        require(endTime_ > startTime_, 'DAOAirdrop: invalid time.');

        bytes32 _hash = ECDSAUpgradeable.toEthSignedMessageHash(
            keccak256(
                abi.encodePacked(
                    block.chainid,
                    msg.sender,
                    airdropId_
                )
            )
        );
        require(IDAOFactory(factoryAddress).isSigner(ECDSAUpgradeable.recover(_hash, signature_)), 'DAOAirdrop: invalid signer.');
        if (token_ == address(0)) {
            require(msg.value == amount_, 'DAOAirdrop: invalid amount.');
        } else {
            IERC20Upgradeable(token_).safeTransferFrom(msg.sender, address(this), amount_);
        }

        airdrops[airdropId_] = Airdrop({
            token: token_,
            tokenStaked: amount_,
            tokenClaimed: 0,
            merkleRoot: bytes32(0),
            startTime: startTime_,
            endTime: endTime_,
            creator: msg.sender
        });

        emit CreateAirdrop(msg.sender, airdropId_, token_, amount_, startTime_, endTime_);
    }

    function settleAirdrop(uint256 airdropId_, uint256 amount_, bytes32 merkleRoot_) external payable {
        Airdrop memory _airdrop = airdrops[airdropId_];
        require(_airdrop.creator == msg.sender, 'DAOAirdrop: not the creator.');
        require(_airdrop.merkleRoot == bytes32(0), 'DAOAirdrop: already settle.');

        if (amount_ > _airdrop.tokenStaked) {
            if (_airdrop.token == address(0)) {
                require(msg.value == amount_ - _airdrop.tokenStaked, 'DAOAirdrop: invalid amount.');
            } else {
                IERC20Upgradeable(_airdrop.token).safeTransferFrom(msg.sender, address(this), amount_ - _airdrop.tokenStaked);
            }
        }

        airdrops[airdropId_].tokenStaked = amount_;
        airdrops[airdropId_].merkleRoot = merkleRoot_;

        emit SettleAirdrop(airdropId_, amount_, merkleRoot_);
    }

    function isClaimed(uint256 airdropId_, address account_) public view returns (bool) {
        return claimedMap[airdropId_][account_];
    }

    function _setClaimed(uint256 airdropId_, address account_, uint256 amount_) private {
        claimedMap[airdropId_][account_] = true;
        airdrops[airdropId_].tokenClaimed += amount_;
    }

    function claimAirdrop(uint256 airdropId_, uint256 index_, address account_, uint256 amount_, bytes32[] calldata merkleProof_) external {
        Airdrop memory _airdrop = airdrops[airdropId_];
        require(_airdrop.creator != address(0), 'DAOAirdrop: not a valid airdrop id.');
        require(block.timestamp > _airdrop.startTime, 'DAOAirdrop: cannot claim yet.');
        require(block.timestamp <= _airdrop.endTime, 'DAOAirdrop: airdrop already done.');
        require(!isClaimed(airdropId_, account_), 'DAOAirdrop: drop already claimed.');
        require(_airdrop.merkleRoot != bytes32(0), 'DAOAirdrop: not settle.');
        require(_airdrop.tokenClaimed + amount_ <= _airdrop.tokenStaked, 'DAOAirdrop: insufficient funds.');

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index_, account_, amount_));
        require(MerkleProofUpgradeable.verify(merkleProof_, _airdrop.merkleRoot, node), 'DAOAirdrop: invalid proof.');

        // Mark it claimed and send the token.
        _setClaimed(airdropId_, account_, amount_);
        if (_airdrop.token == address(0)) {
            AddressUpgradeable.sendValue(payable(account_), amount_);
        } else {
            IERC20Upgradeable(_airdrop.token).safeTransfer(account_, amount_);
        }

        emit Claimed(airdropId_, index_, account_, amount_);
    }

    function recycleAirdrop(uint256 airdropId_) external {
        Airdrop memory _airdrop = airdrops[airdropId_];
        require(_airdrop.creator == msg.sender, 'DAOAirdrop: not the creator.');
        require(block.timestamp > _airdrop.endTime, 'DAOAirdrop: cannot recycle yet.');
        require(_airdrop.tokenStaked > _airdrop.tokenClaimed, 'DAOAirdrop: claimed out.');

        uint256 _reserve = _airdrop.tokenStaked - _airdrop.tokenClaimed;
        airdrops[airdropId_].tokenClaimed = _airdrop.tokenStaked;
        if (_airdrop.token == address(0)) {
            AddressUpgradeable.sendValue(payable(msg.sender), _reserve);
        } else {
            IERC20Upgradeable(_airdrop.token).safeTransfer(_airdrop.creator, _reserve);
        }
    }

}

contracts/DAOBase.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity =0.8.9;

import './interfaces/IDAOBase.sol';
import './interfaces/IDAOFactory.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract DAOBase is OwnableUpgradeable, IDAOBase {

    // @dev constant
    uint256 public constant SETTING_TYPE_GENERAL = 0;
    uint256 public constant SETTING_TYPE_TOKEN = 1;
    uint256 public constant SETTING_TYPE_GOVERNANCE = 2;

    // @dev dao factory address
    address public factoryAddress;

    // @dev DAO Base Info
    General public daoInfo;

    // @dev DAO Token Info
    Token public daoToken;

    // @dev DAO Governance
    Governance public daoGovernance;

    // @dev Manager
    // @dev contract owner: super admin
    mapping(address => bool) public admins;


    // @dev proposals slot
    uint256 public proposalIndex;
    mapping(uint256 => Proposal) public proposals;
    mapping(address => mapping(uint256 => VoteInfo[])) public voteInfos;

    // @dev Event
    event Setting(uint256 indexed settingType);
    event Admin(address indexed admin, bool enable);
    event CreateProposal(uint256 indexed proposalId, address indexed proposer, uint256 startTime, uint256 endTime);
    event CancelProposal(uint256 indexed proposalId);
    event Vote(uint256 indexed proposalId, address indexed voter, uint256 indexed optionIndex, uint256 amount);

    // @dev Struct
    struct Proposal {
        bool cancel;
        address creator;
        string title;
        string introduction;
        string content;
        uint256 startTime;
        uint256 endTime;
        uint256 votingThresholdSnapshot;
        VotingType votingType;
        ProposalOption[] options;
    }

    struct ProposalOption {
        string name;
        uint256 amount;
    }

    struct VoteInfo {
        uint256 index;
        uint256 amount;
    }

    enum SignType { CreateProposal, Voting }
    struct SignInfo {
        uint256 chainId;
        address tokenAddress;
        uint256 balance;
        SignType signType;
        uint256 proposalIdOrDeadline;
    }

    struct ProposalInput {
        string title;
        string introduction;
        string content;
        uint256 startTime;
        uint256 endTime;
        VotingType votingType;
    }


    // @dev Modifier
    /**
     * @dev Throws if called by any account other than the owner or admin.
     */
    modifier onlyOwnerOrAdmin() {
        require(
            owner() == msg.sender || admins[msg.sender],
                "DAOBase: caller is not the owner or admin."
        );
        _;
    }


    //------------------------ initialize ------------------------//
    /**
     * @dev Initializes the contract by simple way.
     */
    function initialize(
        General calldata general_,
        Token calldata token_,
        Governance calldata governance_
    ) override external initializer {
        factoryAddress = msg.sender;
        OwnableUpgradeable.__Ownable_init();

        _setInfo(general_);
        _setToken(token_);
        _setGovernance(governance_);
    }


    //------------------------ owner or admin ------------------------//
    /**
     * @dev remove or add a admin.
     */
    function setAdmin(address admin_, bool enabled_) external onlyOwner {
        _setAdmin(admin_, enabled_);
    }

    /**
     * @dev set dao info
     */
    function setInfo(string[] calldata args) external onlyOwnerOrAdmin {
        require(args.length == 8, 'DAOBase: length mismatch.');
        _setInfo(General(args[0], daoInfo.handle, args[1], args[2], args[3], args[4], args[5], args[6], args[7]));
    }

    function setGovernance(Governance calldata governance_) external onlyOwnerOrAdmin {
        _setGovernance(governance_);
    }


    //------------------------ public ------------------------//
    /**
     * @dev create proposal
     */
    function createProposal(
        ProposalInput calldata input_,  // avoid stack too deep
        string[] calldata options_,
        SignInfo calldata signInfo_,
        bytes calldata signature_
    ) external {
        Governance memory _governance = daoGovernance;
        require(
            input_.votingType != VotingType.Any &&
            (_governance.votingType == VotingType.Any || _governance.votingType == input_.votingType),
                'DAOBase: invalid voting type.'
        );
        require(signInfo_.proposalIdOrDeadline >= block.timestamp, 'DAOBase: expired.');
        require(_verifySignature(signInfo_, signature_), 'DAOBase: invalid signer.');
        require(signInfo_.balance >= _governance.proposalThreshold, 'DAOBase: insufficient balance.');

        uint256 _endTime = input_.endTime;
        if (_governance.votingPeriod > 0) {
            _endTime = input_.startTime + _governance.votingPeriod;
        }
        require(input_.startTime < input_.endTime, 'DAOBase: startTime ge endTime.');
        require(options_.length > 0, 'DAOBase: dont have enough options.');

        uint256 _proposalIndex = proposalIndex;
        proposalIndex = _proposalIndex + 1;
        Proposal storage proposal = proposals[_proposalIndex];
        for (uint256 _index = 0; _index < options_.length; _index++) {
            proposal.options.push(ProposalOption({
                name: options_[_index],
                amount: 0
            }));
        }
        proposal.creator = msg.sender;
        proposal.title = input_.title;
        proposal.introduction = input_.introduction;
        proposal.content = input_.content;
        proposal.startTime = input_.startTime;
        proposal.endTime = _endTime;
        proposal.votingThresholdSnapshot = _governance.votingThreshold;
        proposal.votingType = input_.votingType;

        emit CreateProposal(_proposalIndex, msg.sender, input_.startTime, _endTime);
    }

    /**
     * @dev vote for proposal
     */
    function vote(
        uint256[] calldata optionIndexes_,
        uint256[] calldata amounts_,
        SignInfo calldata signInfo_,
        bytes calldata signature_
    ) external {
        uint256 _proposalId = signInfo_.proposalIdOrDeadline;
        Proposal memory _proposal = proposals[_proposalId];
        require(proposalIndex > _proposalId, 'DAOBase: proposal id not exists.');
        require(optionIndexes_.length == amounts_.length, 'DAOBase: invalid length.');
        require(voteInfos[msg.sender][_proposalId].length == 0, 'DAOBase: already voted.');

        require(_verifySignature(signInfo_, signature_), 'DAOBase: invalid signer.');
        require(block.timestamp >= _proposal.startTime && block.timestamp < _proposal.endTime, 'DAOBase: vote on a wrong time.');
        require(!_proposal.cancel, 'DAOBase: already canceled.');
        if (proposals[_proposalId].votingType == VotingType.Single)
            require(optionIndexes_.length == 1, 'DAOBase: invalid length.');

        uint256 _totalAmount = 0;
        uint256 _optionsLength = proposals[_proposalId].options.length;
        for (uint256 _index = 0; _index < optionIndexes_.length; _index++) {
            require(_optionsLength > optionIndexes_[_index], 'DAOBase: proposal option index not exists.');
            _totalAmount += amounts_[_index];
            proposals[_proposalId].options[optionIndexes_[_index]].amount += amounts_[_index];
            voteInfos[msg.sender][_proposalId].push(VoteInfo(optionIndexes_[_index], amounts_[_index]));

            emit Vote(_proposalId, msg.sender, optionIndexes_[_index], amounts_[_index]);
        }

        require(signInfo_.balance >= _totalAmount, 'DAOBase: insufficient balance.');
    }

    /**
     * @dev Cancel an active proposal
     */
    function cancelProposal(uint256 proposalId_) external {
        Proposal memory _proposal = proposals[proposalId_];
        require(proposalIndex > proposalId_, 'DAOBase: proposal id not exists.');
        require(block.timestamp < _proposal.startTime, 'DAOBase: already started.');
        require(msg.sender == _proposal.creator, 'DAOBase: sender is not the creator.');
        require(!_proposal.cancel, 'DAOBase: already canceled.');

        proposals[proposalId_].cancel = true;
        emit CancelProposal(proposalId_);
    }

    //------------------------ public get ------------------------//
    function getProposalOptionById(uint256 proposalId_) external view returns (ProposalOption[] memory) {
        return proposals[proposalId_].options;
    }

    function getVoteInfoByAccountAndProposalId(address account_, uint256 proposalId_) external view returns (VoteInfo[] memory) {
        return voteInfos[account_][proposalId_];
    }

    //------------------------ private ------------------------//
    function _setAdmin(address admin_, bool enabled_) private {
        admins[admin_] = enabled_;

        emit Admin(admin_, enabled_);
    }

    function _setInfo(General memory general_) private {
        daoInfo = general_;

        emit Setting(SETTING_TYPE_GENERAL);
    }

    function _setToken(Token calldata token_) private {
        daoToken = token_;

        emit Setting(SETTING_TYPE_TOKEN);
    }

    function _setGovernance(Governance calldata governance_) private {
        daoGovernance = governance_;

        emit Setting(SETTING_TYPE_GOVERNANCE);
    }

    function _verifySignature(SignInfo calldata signInfo_, bytes calldata signature_) private view returns (bool) {
        if (signInfo_.chainId != daoToken.chainId || signInfo_.tokenAddress != daoToken.tokenAddress) {
            return false;
        }
        bytes32 _hash = ECDSAUpgradeable.toEthSignedMessageHash(
            keccak256(
                abi.encodePacked(
                    msg.sender,
                    block.chainid,
                    address(this),
                    signInfo_.chainId,
                    signInfo_.tokenAddress,
                    signInfo_.proposalIdOrDeadline,
                    signInfo_.balance,
                    uint256(signInfo_.signType)
                )
            )
        );
        address _signer = ECDSAUpgradeable.recover(_hash, signature_);

        return IDAOFactory(factoryAddress).isSigner(_signer);
    }

    function daoVersion() external pure returns (string memory) {
        return 'v0.2.1';
    }
}

contracts/DAOFactory.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity =0.8.9;

import "./interfaces/IDAOBase.sol";
import "./interfaces/IERC20Base.sol";
import "./interfaces/IDAOFactory.sol";
import '@openzeppelin/contracts/proxy/transparent/ProxyAdmin.sol';
import '@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol';
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import '@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract DAOFactory is OwnableUpgradeable, IDAOFactory {
    using ClonesUpgradeable for address;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    address public proxyAdminAddress;

    address public daoImpl;
    mapping(string => bool) public handles;
    mapping(address => bool) private _signers;
    mapping(address => bool) private _daoAddresses;
    mapping(address => uint256) public nonces;

    address public tokenImpl;
    mapping(address => string) public logoURLs;
    mapping(address => Reserve[]) public reserves;
    mapping(address => address[]) public tokensByAccount;

    event CreateDAO(uint256 indexed handler, address indexed creator, address indexed daoAddress, uint256 chainId, address tokenAddress);

    event CreateERC20(address indexed creator, address token);
    event ClaimReserve(address indexed account, address indexed token, uint256 amount);

    event SignerSet(address indexed account, bool enable);

    constructor() { }

    function initialize(address daoImpl_, address tokenImpl_) external initializer {
        daoImpl = daoImpl_;
        tokenImpl = tokenImpl_;
        OwnableUpgradeable.__Ownable_init();

        ProxyAdmin _proxyAdmin = new ProxyAdmin();
        proxyAdminAddress = address(_proxyAdmin);
    }

    modifier onlyDao() {
        require(_daoAddresses[msg.sender], 'DAOFactory: caller not a dao address.');
        _;
    }

    function setDaoImpl(address daoImpl_) onlyOwner external {
        daoImpl = daoImpl_;
    }

    function setTokenImpl(address tokenImpl_) onlyOwner external {
        tokenImpl = tokenImpl_;
    }

    function setSigner(address signer_, bool enable_) onlyOwner external {
        _signers[signer_] = enable_;

        emit SignerSet(signer_, enable_);
    }

    function isSigner(address signer_) public override view returns (bool) {
        return _signers[signer_];
    }

    function increaseNonce(address account_) onlyDao external returns (uint256 _nonce) {
        _nonce = nonces[account_];
        nonces[account_]++;
    }

    function createDAO(
        IDAOBase.General calldata general_,
        IDAOBase.Token calldata token_,
        IDAOBase.Governance calldata governance_,
        uint256 deadline_,  // block number
        bytes calldata signature_
    ) external {
        require(deadline_ >= block.number, 'DAOFactory: signature was expired.');
        require(!handles[general_.handle], 'DAOFactory: handle is already taken.');

        bytes32 _handleHash = keccak256(abi.encodePacked(general_.handle));
        bytes32 _hash = ECDSAUpgradeable.toEthSignedMessageHash(
            keccak256(
                abi.encodePacked(
                    msg.sender,
                    block.chainid,
                    deadline_,
                    _handleHash
                )
            )
        );
        require(isSigner(ECDSAUpgradeable.recover(_hash, signature_)), 'DAOFactory: invalid signer.');

        // address _daoAddress = daoImpl.clone();
        // IDAOBase(_daoAddress).initialize(general_, token_, governance_);
        // OwnableUpgradeable(_daoAddress).transferOwnership(msg.sender);

        // using proxy
        TransparentUpgradeableProxy _proxy = new TransparentUpgradeableProxy(daoImpl, proxyAdminAddress, '');
        IDAOBase(address(_proxy)).initialize(general_, token_, governance_);
        OwnableUpgradeable(address(_proxy)).transferOwnership(msg.sender);

        handles[general_.handle] = true;
        _daoAddresses[address(_proxy)] = true;

        emit CreateDAO(uint256(_handleHash), msg.sender, address(_proxy), token_.chainId, token_.tokenAddress);
    }

    function upgradeProxy(address payable daoAddress_) external {
        // saving gas
        address _proxyAdminAddress = proxyAdminAddress;
        require(OwnableUpgradeable(daoAddress_).owner() == msg.sender, 'DAOFactory: cannot only upgrade by owner.');
        require(ProxyAdmin(_proxyAdminAddress).getProxyAdmin(TransparentUpgradeableProxy(daoAddress_)) == _proxyAdminAddress, 'DAOFactory: not a valid dao address.');
        require(ProxyAdmin(_proxyAdminAddress).getProxyImplementation(TransparentUpgradeableProxy(daoAddress_)) != daoImpl, 'DAOFactory: already up-to-date.');

        ProxyAdmin(_proxyAdminAddress).upgrade(TransparentUpgradeableProxy(daoAddress_), daoImpl);
    }

    function createERC20(
        string memory name_,
        string memory symbol_,
        string memory logoUrl_,
        uint8 decimal_,
        uint256 totalSupply_,
        DistributionParam[] calldata distributions_
    ) external {
        address _token = tokenImpl.clone();
        IERC20Base(_token).initialize(name_, symbol_, decimal_, totalSupply_);
        logoURLs[_token] = logoUrl_;
        tokensByAccount[msg.sender].push(_token);

        // distribute
        uint256 _distributedAmount = 0;
        for (uint256 index = 0; index < distributions_.length; index++) {
            DistributionParam calldata _distribution = distributions_[index];
            reserves[_distribution.recipient].push(Reserve({
                token: _token,
                amount: _distribution.amount,
                lockDate: _distribution.lockDate
            }));
            _distributedAmount += _distribution.amount;
        }
        if (totalSupply_ > _distributedAmount)
            IERC20Upgradeable(_token).safeTransfer(msg.sender, totalSupply_ - _distributedAmount);
        else
            require(totalSupply_ == _distributedAmount, 'DAOFactory: distributed amount exceed totalSupply');

        emit CreateERC20(msg.sender, _token);
    }

    function claimReserve(uint256 index_) external {
        uint256 _length = reserves[msg.sender].length;
        require(index_ < _length, 'DAOFactory: invalid index.');

        Reserve memory _reserve = reserves[msg.sender][index_];
        require(block.timestamp >= _reserve.lockDate, 'DAOFactory: locked.');
        if (index_ < _length - 1)
            reserves[msg.sender][index_] = reserves[msg.sender][_length - 1];
        reserves[msg.sender].pop();

        IERC20Upgradeable(_reserve.token).safeTransfer(msg.sender, _reserve.amount);
        emit ClaimReserve(msg.sender, _reserve.token, _reserve.amount);
    }

    /** ---------- public getting ---------- **/
    function getReserved(address account_) view public returns (Reserve[] memory) {
        return reserves[account_];
    }

}

contracts/DAOVerifier.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity =0.8.9;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

contract DAOVerifier is OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    uint256 public constant MAX_UINT128 = 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF;
    address public immutable STPT_ADDRESS;

    uint256[] private _verifiedDaoIds;
    // @dev 128 bits: index slot
    // @dev 128 bits: timestamp slot
    mapping(uint256 => uint256) _verifiedDaoIndexesAndTimestamp;

    uint256 public cumulativeStaked;
    uint256 public verificationThreshold;
    mapping(uint256 => uint256) public amountByDaoId;
    mapping(uint256 => mapping(address => uint256)) amountByDaoIdAndAccount;

    mapping(address => uint256[]) private _daoIdsByAccount;


    // @dev response model
    struct StakedDao {
        uint256 daoId;
        uint256 verifiedTimestamp;
        uint256 stakedAmount;
        uint256 stakedAmountTotal;
    }

    constructor(address stptAddress_) {
        STPT_ADDRESS = stptAddress_;
    }

    function initialize(uint256 verificationThreshold_) external initializer {
        verificationThreshold = verificationThreshold_;
        OwnableUpgradeable.__Ownable_init();
    }

    // @dev stake an amount of STPT for ${daoId}
    function stake(uint256 daoId_, uint256 amount_) external {
        uint256 _amountCurrentDao = amountByDaoId[daoId_];
        uint256 _amountCurrentAccount = amountByDaoIdAndAccount[daoId_][msg.sender];

        _amountCurrentDao += amount_;
        if (_amountCurrentDao >= verificationThreshold && _verifiedDaoIndexesAndTimestamp[daoId_] == 0) {
            _verifiedDaoIds.push(daoId_);
            _verifiedDaoIndexesAndTimestamp[daoId_] = (_verifiedDaoIds.length << 128) + block.timestamp;
        }

        cumulativeStaked += amount_;
        amountByDaoId[daoId_] = _amountCurrentDao;
        amountByDaoIdAndAccount[daoId_][msg.sender] = _amountCurrentAccount + amount_;
        if (_amountCurrentAccount == 0 && amount_ > 0) {
            _daoIdsByAccount[msg.sender].push(daoId_);
        }

        IERC20Upgradeable(STPT_ADDRESS).safeTransferFrom(msg.sender, address(this), amount_);
    }

    // @dev un stake
    function unstake(uint256 daoId_, uint256 amount_) external {
        uint256 _amountCurrentAccount = amountByDaoIdAndAccount[daoId_][msg.sender];
        require(_amountCurrentAccount >= amount_, 'DAOVerifier: amount should not exceed staked amount.');

        uint256 _indexAndTimestamp = _verifiedDaoIndexesAndTimestamp[daoId_];
        uint256 _amountCurrentDao = amountByDaoId[daoId_];
        _amountCurrentDao -= amount_;
        if (_amountCurrentDao < verificationThreshold && _indexAndTimestamp > 0) {
            _verifiedDaoIndexesAndTimestamp[daoId_] = 0;

            uint256 _index = (_indexAndTimestamp >> 128) - 1;
            uint256 _tailIndex = _verifiedDaoIds.length - 1;
            if (_index < _tailIndex) {
                uint256 _tailDaoId = _verifiedDaoIds[_tailIndex];
                _verifiedDaoIds[_index] = _tailDaoId;
                _verifiedDaoIndexesAndTimestamp[_tailDaoId] = ((_index + 1) << 128) + (_verifiedDaoIndexesAndTimestamp[_tailDaoId] & MAX_UINT128);
            }
            _verifiedDaoIds.pop();
        }

        cumulativeStaked -= amount_;
        amountByDaoId[daoId_] -= amount_;
        amountByDaoIdAndAccount[daoId_][msg.sender] = _amountCurrentAccount - amount_;
        if (_amountCurrentAccount == amount_) {
            uint256[] memory _stakedDaoIds = _daoIdsByAccount[msg.sender];
            uint256 _stakedDaoLength = _stakedDaoIds.length;
            for (uint256 _index = 0; _index < _stakedDaoLength; _index++) {
                if (daoId_ == _stakedDaoIds[_index]) {
                    if (_index < _stakedDaoLength - 1) {
                        _daoIdsByAccount[msg.sender][_index] = _stakedDaoIds[_stakedDaoLength - 1];
                    }
                    _daoIdsByAccount[msg.sender].pop();
                    break;
                }
            }
        }

        IERC20Upgradeable(STPT_ADDRESS).safeTransfer(msg.sender, amount_);
    }


    /** ---------- public getting ---------- **/
    function getVerifiedDao(address account_) view public returns (StakedDao[] memory) {
        uint256 _daoLength = _verifiedDaoIds.length;
        StakedDao[] memory _result = new StakedDao[](_daoLength);

        for (uint256 _index = 0; _index < _daoLength; _index++) {
            uint256 _daoId = _verifiedDaoIds[_index];
            _result[_index] = StakedDao({
                daoId: _daoId,
                verifiedTimestamp: _verifiedDaoIndexesAndTimestamp[_daoId] & MAX_UINT128,
                stakedAmount: amountByDaoIdAndAccount[_daoId][account_],
                stakedAmountTotal: amountByDaoId[_daoId]
            });
        }

        return _result;
    }

    function getVerifiedDaoByIds(uint256[] calldata daoIds_, address account_) view public returns (StakedDao[] memory) {
        uint256 _daoLength = daoIds_.length;
        StakedDao[] memory _result = new StakedDao[](_daoLength);

        for (uint256 _index = 0; _index < _daoLength; _index++) {
            uint256 _daoId = daoIds_[_index];
            _result[_index] = StakedDao({
                daoId: _daoId,
                verifiedTimestamp: _verifiedDaoIndexesAndTimestamp[_daoId] & MAX_UINT128,
                stakedAmount: amountByDaoIdAndAccount[_daoId][account_],
                stakedAmountTotal: amountByDaoId[_daoId]
            });
        }

        return _result;
    }

    function getMyStakedDao(address account_) view public returns (StakedDao[] memory) {
        uint256[] memory _daoIds = _daoIdsByAccount[account_];
        uint256 _daoLength = _daoIds.length;
        StakedDao[] memory _result = new StakedDao[](_daoLength);

        for (uint256 _index = 0; _index < _daoLength; _index++) {
            uint256 _daoId = _daoIds[_index];
            _result[_index] = StakedDao({
                daoId: _daoId,
                verifiedTimestamp: _verifiedDaoIndexesAndTimestamp[_daoId] & MAX_UINT128,
                stakedAmount: amountByDaoIdAndAccount[_daoId][account_],
                stakedAmountTotal: amountByDaoId[_daoId]
            });
        }

        return _result;
    }

}

